<?phpif ( $_REQUEST['link_id'] ) {	$sql = "SELECT * FROM Links WHERE id = ".ReturnSecureString($_REQUEST['link_id']);	$errtxt .= '<br />'.$sql;	$rL = mysql_query($sql);	if ( mysql_num_rows($rL)>0 ) {		$resL = mysql_fetch_assoc($rL);			$link_id = $resL['id'];		$target_group = $resL['group_id'];		$errtxt .= '<br/>target: '.$target_group;		$URL = $resL['URL'];		$title = $resL['title'];		$row = $resL['row'];		$col = $resL['col'];	} else {		$link_id = $_REQUEST['id'];		$target_group = $_REQUEST['group_id'];		$URL = $_REQUEST['URL'];		$title = $_REQUEST['title'];		$row = $_REQUEST['row'];		$col = $_REQUEST['col'];	}}?>	<form action="config/links.php" method="post" id="new_link_form" name="new_link_form">			<table border="0" cellpadding="2">				<tr>					<td>Link / URL: </td>					<td><input name="URL" id="URL" type="text" size="40" maxlength="255" tabindex="1" value="<?php echo $URL ?>" /></td>					<td>Group: </td>					<td><select id="group_id" tabindex="3" name="group_id">						<!-- /// on elect of this option, hide new_link_form.new_group -->						<option label="add to a new group: " value="/new/">add to a new group: </option>						<?php $sql = "SELECT G.id AS id, G.Title AS Title FROM Groups G INNER JOIN GroupsPages GP ON G.id = GP.group_id INNER JOIN Links L ON GP.group_id = L.group_id WHERE L.user_id = GP.user_id AND L.user_id = ".$_SESSION['user_id']." AND GroupType_id < 2 GROUP BY G.Title";							$errtxt .= '<br />'.$sql;							$resG = mysql_query($sql);							if (mysql_num_rows($resG)>0) {								echo '					<optgroup label="Your Groups">';								while ($r = mysql_fetch_assoc($resG)) { 																// determine whether to draw ' selected' label								if ( $r['id'] == $target_group)  {									$sel = ' selected';								} else {									$sel = '';								}								?>						<option label="<?php echo $r['Title'] ?>" value="<?php echo $r['id']?>" <?php echo $sel?>><?php echo $r['Title']?></option>							<?php 	} ?>						</optgroup>					<?php								}													?>						<optgroup label="System Groups">						<?php $sql = "SELECT G.id AS id, G.Title AS Title FROM Groups G WHERE GroupType_id = 2 ORDER BY G.Title";							$errtxt .= $sql;							$resG = mysql_query($sql);							while ($r = mysql_fetch_assoc($resG)) {								// determine whether to draw ' selected' label								if ( $r['id'] == $target_group)  {									$sel = ' selected';								} else {									$sel = '';								}							?>							<option label="<?php echo $r['Title']?>" value="<?php echo $r['id'] ?>" <?php echo $sel?>><?php echo $r['Title']?></option>	<?php							}						?>						</optgroup>					</select>					</td>										<td><a href="<?php echo $site_uri.substr($_SERVER['SCRIPT_NAME'], 1) ?>">new link</a> | <a href="<?php echo $site_uri.$site_path?>config/links.php?proc=delete&link_id=<?php echo $link_id?>">delete</a></td>				</tr>				<tr>					<td>Title: </td>					<td><input name="title" id="title" type="text" size="35" maxlength="45" tabindex="2" value="<?php echo $title ?>" /></td>					<td></td>					<td>						<input name="new_group" id="new_group" type="text" size="20" maxlength="30" tabindex="6" />					</td>					<td><input name="submit" type="submit" value="Send it" /></td>				</tr>			</table>			<input name="row" id="row" type="hidden" size="2" maxlength="2" tabindex="4" value="<?php echo $row ?>" />			<input name="col" id="col" type="hidden" size="2" maxlength="2" tabindex="5" value="<?php echo $col ?>" />						<input name="link_id" type="hidden" value="<?php echo $link_id?>" />			<input name="proc" type="hidden" value="update" />		</form>	