<?php If ( $_REQUEST['err'] || $_REQUEST['msg'] || $_SESSION['msg'] ) { 	if ( $_REQUEST['err'] ) { $msg = $_REQUEST['err']; }	else if ( $_REQUEST['msg'] ) { $msg = $_REQUEST['msg']; }	else if ( $_SESSION['msg'] ) { $msg = $_SESSION['msg']; }	else { $msg = ''; }	$_SESSION['msg'] = ''; ?>	<div class="notify_on">		 <?php echo $msg; ?>	</div><?php } else { ?>	<div class="notify_text">All things working well	</div><?php }?>